CREATE TABLE task (
  id INT PRIMARY KEY,
  title VARCHAR,
  description VARCHAR,
  priority VARCHAR,
  status VARCHAR,
  comments VARCHAR
);
CREATE TABLE person (
  id INT PRIMARY KEY,
  name VARCHAR,
  email VARCHAR,
  role VARCHAR
);
