CREATE TABLE document (
  id INT PRIMARY KEY,
  title VARCHAR,
  content VARCHAR,
  status VARCHAR,
  created\_at VARCHAR,
  updated\_at VARCHAR
);
CREATE TABLE author (
  id INT PRIMARY KEY,
  name VARCHAR,
  email VARCHAR,
  role VARCHAR
);
CREATE TABLE manager (
  id INT PRIMARY KEY,
  name VARCHAR,
  email VARCHAR,
  role VARCHAR
);
CREATE TABLE validationcommittee (
