@startuml
left to right direction

actor "Superadministrateur" as superadmin
actor "Administrateur de compte client" as adminclient
actor "Utilisateur final" as userfinal
actor "Manager direct" as managerdirect
actor "Comité de validation" as committeevalidation

package "Tableau de bord" {
  usecase "Affichage du tableau de bord pour l'administrateur" as FR1
}

package "Cycle de vie des documents" {
  usecase "Gestion des versions des documents" as FR2
  usecase "Gestion des documents" as FR6
}

package "Rapports de gestion" {
  usecase "Affichage des rapports de gestion" as FR3
}

package "Recherche avancée" {
  usecase "Recherche avancée des documents" as FR4
}

package "Documentation validation process" {
  usecase "Validation des documents" as FR5
}

superadmin --> FR1
adminclient --> FR2
managerdirect --> FR6
userfinal --> FR2, FR6
committeevalidation --> FR3, FR5

FR1 ..> FR2 : <<include>>
FR2 ..> FR6 : <<extend>>
FR3 ..> FR5 : <<extend>>

@enduml

@startuml
|ManagerDirect|
start

:Activity from requirements;

:Another activity;

if (Condition based on requirements ?) then (yes)
  :Action if true;

  |Client|
  :Another actor's action;

else (no)
  :Action if false;
  stop
endif

|ManagerDirect|
:Final activity;

stop
@enduml
